ALTER PROCEDURE [GetMotivosDeReclamoPorServicio]
    @servicioId [int]
AS
BEGIN
     	
    Select tm.Id,tm.Nombre,tm.tipoProceso_Id,tm.Activo,ts.Nombre as SERVICIO 
    from tMotivoDeReclamo tm 
    inner join
    tServicios ts on
    tm.servicio_Id = ts.Id
    and ts.Id = @servicioId
    
END
GO

ALTER PROCEDURE [GetMotivosDeReclamoPorNombre]
    @nombre [nvarchar](max)
AS
BEGIN
     	
    Select tm.Id,tm.Nombre,tm.tipoProceso_Id,tm.Activo,ts.Nombre as SERVICIO 
    from tMotivoDeReclamo tm 
    inner join
    tServicios ts on
    tm.servicio_Id = ts.Id
    where tm.Nombre like '%'+ @nombre +'%'
    
END
