CREATE PROCEDURE [GetLocalidadesPorProvincia]
    @provinciaId [int]
AS
BEGIN
     	
    SELECT loc.Id,loc.Nombre,prov.Nombre,loc.Activo 
    	from tLocalidades loc
    	                inner join
    tProvincias prov
    	                on loc.PROVINCIA_ID = prov.Id
    	                and loc.PROVINCIA_ID = @provinciaId
    
END
