CREATE PROCEDURE [GetLocalidadesPorId]
    @id [int]
AS
BEGIN
     	
    SELECT loc.Id,loc.Nombre,prov.Nombre as PROVINCIA,loc.Activo
    from tLocalidades loc
    inner join
    tProvincias prov
    on loc.PROVINCIA_ID = prov.Id
    and loc.Id = @id
    
END
