CREATE PROCEDURE [GetLocalidadesPorNombreyProvincia]
    @nombre [nvarchar](max),
    @provinciaId [int]
AS
BEGIN
     	
    SELECT loc.Id,loc.Nombre,prov.Nombre,loc.Activo 
    	from tLocalidades loc
    	                inner join
    tProvincias prov
    	                on loc.PROVINCIA_ID = prov.Id
    	                where loc.Nombre like '%'+ @nombre +'%'
    and prov.Id = @provinciaId
    
END
